def get_event_cards():
    """Return the list of event cards"""
    return EVENT_CARDS.copy()

def get_active_cards():
    """Return only active event cards"""
    return [card for card in EVENT_CARDS if card["status"] == "Active"]

def get_cards_by_box(box_name):
    """Return cards from a specific box"""
    return [card for card in EVENT_CARDS if card["box"] == box_name]

def get_cards_by_status(status):
    """Return cards with a specific status"""
    return [card for card in EVENT_CARDS if card["status"] == status]
